mapper.select:
SELECT cmo.*,cm.* FROM cs_membercard_order cmo LEFT JOIN cs_member_card cm ON cmo.`membercard_id`=cm.id where 1=1 and cmo.wxuser_id = #{param.wxuserId}
and cmo.membercard_id = #{param.membercardId}
AND CURTIME() >= cmo.start_time AND CURTIME() <= cmo.end_time ANd cmo.payment_status = 2

mapper.update:
update cs_membercard_order set status = #{param.status}
where deleted = 0

    and id = #{param.id}

update cs_membercard_order set payment_status = #{param.paymentStatus}

    ,payment_msg = #{param.paymentMsg}

where deleted = 0
and out_trade_no = #{param.outTradeNo}
and payment_status != #{param.paymentStatus}
